mapper.select:
select 

from t_vehicleinfo

     and t_vehicleinfo.lastDataUpTime = #{lastDataUpTime}
     and t_vehicleinfo.status = #{status}
     and t_vehicleinfo.motorType = #{motorType}
     and t_vehicleinfo.airBottleCap = #{airBottleCap}
     and t_vehicleinfo.vehicleSN = #{vehicleSN}
     and t_vehicleinfo.batteryCap = #{batteryCap}
     and t_vehicleinfo.engineVinNo = #{engineVinNo}
     and t_vehicleinfo.online = #{online}
     and t_vehicleinfo.producer = #{producer}
     and t_vehicleinfo.doorpumpModel = #{doorpumpModel}
     and t_vehicleinfo.dynamoModel = #{dynamoModel}
     and t_vehicleinfo.paymentExpiredDate = #{paymentExpiredDate}
     and t_vehicleinfo.absModel = #{absModel}
     and t_vehicleinfo.vehicleNo = #{vehicleNo}
     and t_vehicleinfo.lastAlarmTime = #{lastAlarmTime}
     and t_vehicleinfo.oilCost = #{oilCost}
     and t_vehicleinfo.heaterSupplier = #{heaterSupplier}
     and t_vehicleinfo.deptId = #{deptId}
     and t_vehicleinfo.batteryModel = #{batteryModel}
     and t_vehicleinfo.seatSupplier = #{seatSupplier}
     and t_vehicleinfo.oilBoxCap = #{oilBoxCap}
     and t_vehicleinfo.fsbSupplier = #{fsbSupplier}
     and t_vehicleinfo.engineModel = #{engineModel}
     and t_vehicleinfo.isReparing = #{isReparing}
     and t_vehicleinfo.paymentStatus = #{paymentStatus}
     and t_vehicleinfo.vehicleType = #{vehicleType}
     and t_vehicleinfo.eleCost = #{eleCost}
     and t_vehicleinfo.expireDate = #{expireDate}
     and t_vehicleinfo.gasCost = #{gasCost}
     and t_vehicleinfo.alarm = #{alarm}
     and t_vehicleinfo.motorSupplier = #{motorSupplier}
     and t_vehicleinfo.sim = #{sim}
     and t_vehicleinfo.lineInfo = #{lineInfo}
     and t_vehicleinfo.gearBoxModel = #{gearBoxModel}
     and t_vehicleinfo.dayRunTime = #{dayRunTime}
     and t_vehicleinfo.tireModel = #{tireModel}
     and t_vehicleinfo.deviceId = #{deviceId}
     and t_vehicleinfo.tirepressureSupplier = #{tirepressureSupplier}
     and t_vehicleinfo.wiperSupplier = #{wiperSupplier}
     and t_vehicleinfo.electricboxModel = #{electricboxModel}
     and t_vehicleinfo.brakeshoreModel = #{brakeshoreModel}
     and t_vehicleinfo.wheelaxlesModel = #{wheelaxlesModel}
     and t_vehicleinfo.purchaseDate = #{purchaseDate}
     and t_vehicleinfo.lightswitchsupplier = #{lightswitchsupplier}
     and t_vehicleinfo.deviceFactoryNo = #{deviceFactoryNo}
     and t_vehicleinfo.vehicleId = #{vehicleId}

mapper.update:
update t_vehicleinfo set
    deviceFactoryNo=#{deviceFactoryNo}
    ,lightswitchsupplier=#{lightswitchsupplier}
    ,purchaseDate=#{purchaseDate}
    ,wheelaxlesModel=#{wheelaxlesModel}
    ,brakeshoreModel=#{brakeshoreModel}
    ,electricboxModel=#{electricboxModel}
    ,wiperSupplier=#{wiperSupplier}
    ,tirepressureSupplier=#{tirepressureSupplier}
    ,deviceId=#{deviceId}
    ,tireModel=#{tireModel}
    ,dayRunTime=#{dayRunTime}
    ,gearBoxModel=#{gearBoxModel}
    ,lineInfo=#{lineInfo}
    ,sim=#{sim}
    ,motorSupplier=#{motorSupplier}
    ,alarm=#{alarm}
    ,gasCost=#{gasCost}
    ,expireDate=#{expireDate}
    ,eleCost=#{eleCost}
    ,vehicleType=#{vehicleType}
    ,paymentStatus=#{paymentStatus}
    ,isReparing=#{isReparing}
    ,engineModel=#{engineModel}
    ,fsbSupplier=#{fsbSupplier}
    ,oilBoxCap=#{oilBoxCap}
    ,seatSupplier=#{seatSupplier}
    ,batteryModel=#{batteryModel}
    ,deptId=#{deptId}
    ,heaterSupplier=#{heaterSupplier}
    ,oilCost=#{oilCost}
    ,lastAlarmTime=#{lastAlarmTime}
    ,vehicleNo=#{vehicleNo}
    ,absModel=#{absModel}
    ,paymentExpiredDate=#{paymentExpiredDate}
    ,dynamoModel=#{dynamoModel}
    ,doorpumpModel=#{doorpumpModel}
    ,producer=#{producer}
    ,online=#{online}
    ,engineVinNo=#{engineVinNo}
    ,batteryCap=#{batteryCap}
    ,vehicleSN=#{vehicleSN}
    ,airBottleCap=#{airBottleCap}
    ,motorType=#{motorType}
    ,status=#{status}
    ,lastDataUpTime=#{lastDataUpTime}
where vehicleId=#{vehicleId}
